// ListEmployeeComponent.js

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import EmployeeService from '../service/EmployeeService';

const ListEmployeeComponent = () => {
    const [employeeArray, setEmployeeArray] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        getAllEmployee();
    }, []);

    const getAllEmployee = () => {
        EmployeeService.getAllEmployee()
            .then(res => setEmployeeArray(res.data))
            .catch(error => console.log(error));
    };

    const deleteEmployee = (e, id) => {
        e.preventDefault();
        EmployeeService.deleteEmployee(id)
            .then(getAllEmployee())
            .catch(error => console.log(error));
    };

    const handleChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const filteredEmployees = employeeArray.filter(employee =>
        employee.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.designation.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className='container'>
            <Link to={"/add-employee"} className='btn btn-primary mb-2 mt-3' href="">Add Employee</Link>
            <input
                type="text"
                placeholder="Search by name or designation"
                className="form-control mb-2"
                value={searchTerm}
                onChange={handleChange}
            />
            <h2 className='text-center mb-4'>List Employee</h2>
            <table className='table table-bordered table striped'>
                <thead>
                    <th>Employee ID</th>
                    <th>Employee First Name</th>
                    <th>Employee Last Name</th>
                    <th>Employee Email</th>
                    <th>Actions</th>
                </thead>
                <tbody>
                    {filteredEmployees.map(employee =>
                        <tr key={employee.id}>
                            <td>{employee.id}</td>
                            <td>{employee.firstName}</td>
                            <td>{employee.lastName}</td>
                            <td>{employee.email}</td>
                            <td>
                                <Link to={`/add-employee/${employee.id}`} className='btn btn-info'>Update</Link>
                                <button onClick={(e) => deleteEmployee(e, employee.id)} className='btn btn-danger ml-2'>Delete</button>
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ListEmployeeComponent;
